INSERT INTO `wp_posts` VALUES(342, 1, '2009-07-06 11:20:00', '2009-07-06 11:20:00', '<a href="http://2.bp.blogspot.com/_2-fKtUFjc8Y/SlHeJZeJqaI/AAAAAAAAAA0/4cfpE_Er8RY/s1600-h/Dolle.jpg" onblur="try {parent.deselectBloggerImageGracefully();} catch(e) {}"><img alt="" border="0" id="BLOGGER_PHOTO_ID_5355305684957768098" src="http://2.bp.blogspot.com/_2-fKtUFjc8Y/SlHeJZeJqaI/AAAAAAAAAA0/4cfpE_Er8RY/s320/Dolle.jpg" style="cursor: pointer; float: left; height: 320px; margin: 0pt 10px 10px 0pt; width: 240px;" /></a><span style="font-size: 85%;"><br /></span><span style="font-size: 85%;"><span style="font-family: verdana;">Again! Dolle is back with his super charm and personality. Not to mention the black original stamp tee that he is wearing to support his great look:-)<br /><br />This picture was shot on the 29th of May during the Sustainable Festival in Copenhagen organized by DanChurchAid, in which A QUESTION OF participated.<br /><br />If you are not aware, Dolle is our graphic designer!<br /></span></span>', 'Dolle and his Original Stamp tee in the city center of Copenhagen', '', 'inherit', 'closed', 'closed', '', '55-revision', '', '', '2009-07-06 11:20:00', '2009-07-06 11:20:00', '', 55, 'http://localhost:8888/aquestionof/site/blog/55-revision/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(343, 1, '2009-07-04 10:23:00', '2009-07-04 10:23:00', '<a onblur="try {parent.deselectBloggerImageGracefully();} catch(e) {}" href="http://3.bp.blogspot.com/_2-fKtUFjc8Y/Sk8t6BRZ4QI/AAAAAAAAAAc/Rqb_EWCaCgs/s1600-h/PinkFox+2.jpg"><img style="margin: 0pt 10px 10px 0pt; float: left; cursor: pointer; width: 240px; height: 320px;" src="http://3.bp.blogspot.com/_2-fKtUFjc8Y/Sk8t6BRZ4QI/AAAAAAAAAAc/Rqb_EWCaCgs/s320/PinkFox+2.jpg" alt="" id="BLOGGER_PHOTO_ID_5354548956763906306" border="0" /></a><br /><span style=";font-family:verdana;font-size:85%;"  >Pink Fox (picture) is a minor design team that is using A QUESTION OF organic fair trade T-shirts. Some of the profit from their sales will be used to support prostitutes in South America. Will we keep you updated with the development of this project, and we soon expect the T-shirts to be available on our webshop. Thanks!</span>', 'Pink Fox design on A QUESTION OF T-shirts', '', 'inherit', 'closed', 'closed', '', '54-revision', '', '', '2009-07-04 10:23:00', '2009-07-04 10:23:00', '', 54, 'http://localhost:8888/aquestionof/site/blog/54-revision/', 0, 'revision', '', 0);
